import React, {Component} from "react";

class Status extends Component {

    state = {
        "o2a_variables": {

            "XFRAStatus": "Online",
            "T7Status": "Online",
            "GmcVersion": "081.390.000",
            "bunny_XFRA": "081.003.090",
        }
    }

    getStyleForStatus = () => {
        return {
            backgroundColor : "",
            padding: '10px',
            borderBottom: '1px #ccc dotted',

        }
    }

    getStyleForTextT7 = () => {
        return {
            textAlign: "center",
            color: this.state.o2a_variables.T7Status == "Online"? "#008000" : "#FF0000"
        }
    }

    getStyleForTextXFRA = () => {
        return {
            textAlign: "center",
            color: this.state.o2a_variables.XFRAStatus == "Online"? "#008000" : "#FF0000"
        }
    }




    render() {
        return (
            <div style={this.getStyleForStatus()}>
                <h1 style={this.getStyleForTextT7()}> T7 STD: {this.state.o2a_variables.T7Status} </h1>
                <span><h6 style={this.getStyleForTextXFRA()}>BE: {this.state.o2a_variables.bunny_XFRA}</h6>
                    <h6 style={this.getStyleForText()}>GMC: {this.state.o2a_variables.GmcVersion}</h6>
                    <h6 style={this.getStyleForText()}>Bunny: {this.state.o2a_variables.GmcVersion}</h6></span>
            </div>
        )
    }
}

export default Status
